mapper.select:
	SELECT 
			TAII.ID,
			TAII.PARENTID,
			TAII.SYSTEMID,
			TAII.DESCRIPTION,
			TAII.NAME,
			TAII.VALID,
			TAII.EDITABLE,
			TAII.VIEWABLE,
			TAII.AUTHTYPE
	  FROM AUTH_AUTHITEM${tableSuffix} TAII
	
		  
            AND TAII.ID = #{id,javaType=java.lang.String}
        
          
            AND TAII.SYSTEMID = #{systemId,javaType=java.lang.String}
        
		  
            AND TAII.VALID = #{valid,javaType=boolean}
        
		  
            AND TAII.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TAII.EDITABLE = #{editAble,javaType=boolean}
        
		  
            AND TAII.VIEWABLE = #{viewAble,javaType=boolean}
        
		  
            AND TAII.AUTHTYPE = #{authType,javaType=java.lang.String}
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY TAII.AUTHTYPE,TAII.ID

	SELECT COUNT(1)
	  FROM AUTH_AUTHITEM${tableSuffix} TAII
	
		  
            AND TAII.ID = #{id,javaType=java.lang.String}
        
          
            AND TAII.SYSTEMID = #{systemId,javaType=java.lang.String}
        
		  
            AND TAII.ISVALID = #{isValid,javaType=boolean}
        
		  
            AND TAII.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TAII.ISEDITABLE = #{isEditAble,javaType=boolean}
        
		  
            AND TAII.ISVIEWABLE = #{isViewAble,javaType=boolean}
        
		  
            AND TAII.AUTHTYPE = #{authType,javaType=java.lang.String}

mapper.delete:
DELETE FROM AUTH_AUTHITEM${tableSuffix} TAII 
 WHERE TAII.ID = #{authItem.id}
   AND TAII.ID = #{authItem.systemId}
