mapper.select:
SELECT TFD.id, TFD.system, TFD.module, TFD.relativePath, TFD.filename, TFD.filenameExtension, TFD.viewUrl, TFD.deleteDate, TFD.createDate, TFD.lastUpdateDate FROM CORE_FILE_DEFINITION TFD WHERE
AND TFD.id = #{id}
AND TFD.relativePath = #{relativePath} AND TFD.module = #{module}

	SELECT 
			TFD.id,
			TFD.system,
			TFD.module,
			TFD.relativePath,
			TFD.filename,
			TFD.filenameExtension,
			TFD.viewUrl,
			TFD.deleteDate,
			TFD.createDate,
			TFD.lastUpdateDate
	  FROM CORE_FILE_DEFINITION TFD


            AND TFD.id = #{id}


            AND TFD.system = #{system,javaType=java.lang.String}


            AND TFD.module = #{module,javaType=java.lang.String}


            AND TFD.relativePath = #{relativePath}


            AND TFD.relativePath LIKE concat(#{relativeFolder,jdbcType=VARCHAR}, '%')


            AND TFD.filename = #{filename,javaType=java.lang.String}


            AND TFD.filenameExtension = #{filenameExtension,javaType=java.lang.String}


        	AND TFD.filenameExtension in #{filenameExtensionTemp,javaType=java.lang.String}




            ORDER BY ${orderSql}


            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM CORE_FILE_DEFINITION TFD


            AND TFD.id = #{id}


            AND TFD.system = #{system,javaType=java.lang.String}


            AND TFD.module = #{module,javaType=java.lang.String}


            AND TFD.relativePath = #{relativePath}


            AND TFD.relativePath LIKE concat(#{relativeFolder,jdbcType=VARCHAR}, '%')


            AND TFD.filename = #{filename,javaType=java.lang.String}


            AND TFD.filenameExtension = #{filenameExtension,javaType=java.lang.String}


        	AND TFD.filenameExtension in #{filenameExtensionTemp,javaType=java.lang.String}
