CREATE TABLE core_file_definition_bak AS SELECT * FROM core_file_definition;
DROP TABLE core_file_definition;
CREATE TABLE core_file_definition(
	id varchar(64),
	system varchar(64),
	module varchar(64),
	relativePath varchar(256),
	filename varchar(64),
	filenameExtension varchar(64),
	lastUpdateDate datetime,
	createDate datetime,
	deleteDate datetime,
	primary key(id)
);
CREATE UNIQUE INDEX idx_file_definition_00 ON core_file_definition(relativePath);

