mapper.select:
SELECT DD.id, DD.code, DD.lastUpdateDate, DD.name, DD.type, DD.valid, DD.createDate, DD.modifyAble, DD.parentId, DD.remark, DD.attributes FROM BD_DATA_DICT DD WHERE
AND DD.id = #{id}
AND DD.type = #{type}
AND DD.code = #{code}

	SELECT 
			DD.id,
			DD.code,
			DD.lastUpdateDate,
			DD.name,
			DD.type,
			DD.valid,
			DD.createDate,
			DD.modifyAble,
			DD.parentId,
			DD.remark,
			DD.attributes
	  FROM BD_DATA_DICT DD
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		
			AND DD.parentId IN #{parentIdTemp}
        
		  
             AND DD.type = #{type} 
        
          
             AND DD.parentId = #{parentId} 
        
          
             AND DD.code = #{code} 
        
		  
             AND DD.modifyAble = #{modifyAble} 
        
		  
             AND DD.valid = #{valid} 
        
		  
             AND DD.remark = #{remark} 
        
		  
             AND DD.name = #{name} 
        
		
			 AND DD.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY type,createDate DESC

	SELECT COUNT(1)
	  FROM BD_DATA_DICT DD
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		
			AND DD.parentId IN #{parentIdTemp}
        
		  
             AND DD.type = #{type} 
        
          
             AND DD.parentId = #{parentId} 
        
          
             AND DD.code = #{code} 
        
		  
             AND DD.modifyAble = #{modifyAble} 
        
		  
             AND DD.valid = #{valid} 
        
		  
             AND DD.remark = #{remark} 
        
		  
             AND DD.name = #{name} 
        
		
			 AND DD.id <> #{excludeId}

mapper.delete:
	DELETE FROM BD_DATA_DICT WHERE
	
		  
            AND id = #{id}
        
		  
            AND parentId = #{parentId}
        
          
            AND code = #{code,jdbcType=VARCHAR}
        
          
            AND type = #{type,jdbcType=VARCHAR}
