mapper.select:
SELECT TDD.id, TDD.parentId, TDD.code, TDD.name, TDD.value, TDD.module, TDD.remark, TDD.validateExpression, TDD.modifyAble, TDD.leaf, TDD.attributes, TDD.lastUpdateDate, TDD.createDate FROM bd_config_context TDD WHERE
AND TDD.id = #{id}
AND TDD.code = #{code,jdbcType=VARCHAR}
AND TDD.module = #{module,jdbcType=VARCHAR}

	SELECT 
			TDD.id,
			TDD.parentId,
			TDD.code,
			TDD.name,
			TDD.value,
			TDD.module,
			TDD.remark,
			TDD.validateExpression,
			TDD.modifyAble,
			TDD.leaf,
			TDD.attributes,
			TDD.lastUpdateDate,
			TDD.createDate
	  FROM bd_config_context TDD
	
		
			AND TDD.parentId IN #{parentIdTemp,jdbcType=VARCHAR}
        
		  
             AND TDD.parentId = #{parentId,jdbcType=VARCHAR} 
        
          
             AND TDD.code = #{code,jdbcType=VARCHAR} 
        
          
             AND TDD.module = #{module,jdbcType=VARCHAR} 
        
		  
             AND TDD.modifyAble = #{modifyAble,jdbcType=BIT} 
        
		  
             AND TDD.leaf = #{leaf,jdbcType=BIT} 
        
          
             AND TDD.name = #{name,jdbcType=VARCHAR} 
        
		
			 AND TDD.id <> #{excludeId,javaType=java.lang.String} 
		
	
	ORDER BY createDate

	SELECT COUNT(1)
	  FROM BD_DATA_DICT TDD
	
		
			AND TDD.parentId IN #{parentIdTemp,jdbcType=VARCHAR}
        
		  
             AND TDD.parentId = #{parentId,jdbcType=VARCHAR} 
        
          
             AND TDD.code = #{code,jdbcType=VARCHAR} 
        
          
             AND TDD.module = #{module,jdbcType=VARCHAR} 
        
		  
             AND TDD.modifyAble = #{modifyAble,jdbcType=BIT} 
        
		  
             AND TDD.leaf = #{leaf,jdbcType=BIT} 
        
          
             AND TDD.name = #{name,jdbcType=VARCHAR} 
        
		
			 AND TDD.id <> #{excludeId,javaType=java.lang.String}

mapper.delete:
	DELETE FROM bd_config_context WHERE
	
		  
            AND parentId = #{parentId}
        
		  
            AND id = #{id}
        
          
            AND code = #{code,jdbcType=VARCHAR}
        
          
             AND module = #{module,jdbcType=VARCHAR}
