mapper.select:
	SELECT 
			PI.id,
			PI.catalog,
			PI.prefix,
			PI.name,
			PI.version,
			PI.installed,
			PI.valid,
			PI.author,
			PI.priority,
			PI.remark,
			PI.lastUpdateDate,
			PI.createDate,
			PI.settingUrl,
			PI.describeUrl,
			PI.installUrl,
			PI.uninstallUrl
	  FROM BD_PLUGIN_INSTANCE PI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND PI.id = #{id} 
        
          
             AND PI.catalog = #{catalog} 
        				
		  
             AND PI.name = #{name} 
        			
		  
             AND PI.valid = #{valid} 
        			
		  
             AND PI.author = #{author} 
        			
		  
             AND PI.createDate >= #{minCreateDate} 
        
		  
             AND PI.createDate < #{maxCreateDate} 
        
		  
             AND PI.prefix = #{prefix} 
        			
		  
             AND PI.remark = #{remark} 
        			
		  
             AND PI.installUrl = #{installUrl} 
        			
		  
             AND PI.settingUrl = #{settingUrl} 
        			
		  
             AND PI.version = #{version} 
        			
		  
             AND PI.describeUrl = #{describeUrl} 
        			
		  
             AND PI.priority = #{priority} 
        			
		  
             AND PI.installed = #{installed} 
        		
		  
             AND PI.uninstallUrl = #{uninstallUrl} 
        			
		
			 AND PI.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM BD_PLUGIN_INSTANCE PI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND PI.id = #{id} 
        	
          
             AND PI.catalog = #{catalog} 
        			
		  
             AND PI.name = #{name} 
        			
		  
             AND PI.valid = #{valid} 
        			
		  
             AND PI.author = #{author} 
        			
		  
             AND PI.createDate >= #{minCreateDate} 
        
		  
             AND PI.createDate < #{maxCreateDate} 
        		
		  
             AND PI.prefix = #{prefix} 
        			
		  
             AND PI.remark = #{remark} 
        			
		  
             AND PI.installUrl = #{installUrl} 
        			
		  
             AND PI.settingUrl = #{settingUrl} 
        			
		  
             AND PI.version = #{version} 
        			
		  
             AND PI.describeUrl = #{describeUrl} 
        			
		  
             AND PI.priority = #{priority} 
        			
		  
             AND PI.installed = #{installed} 
        		
		  
             AND PI.uninstallUrl = #{uninstallUrl} 
        			
		
			 AND PI.id <> #{excludeId}
