mapper.select:
	SELECT 
			ARI.id,
			ARI.refId,
			ARI.refType,
			ARI.authId,
			ARI.createDate,
			ARI.createOperatorId,
			ARI.expiryDate,
			ARI.effectiveDate,
			ARI.lastUpdateOperatorId,
			ARI.lastUpdateDate
	  FROM SEC_AUTHREF ARI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND ARI.id = #{id} 
        			
		  
             AND ARI.refId = #{refId} 
        			
		  
             AND ARI.refType = #{refType} 
        
        
        	AND
        	
        		
        			ARI.refType = #{refTypeTemp,javaType=java.lang.String}
        			AND ARI.refId IN #{refIdTemp,javaType=java.lang.String}
        		
        	
        		
		  
             AND ARI.authId = #{authId} 
        		
          
        	  
		          
		             AND ((ARI.expiryDate IS NULL OR ARI.expiryDate <= now(6)) AND (ARI.effectiveDate IS NULL OR ARI.effective >= now(6))) 
		        
		          
		             AND ((ARI.expiryDate IS NOT NULL AND ARI.expiryDate > now(6)) AND (ARI.effectiveDate IS NOT NULL OR ARI.effective < now(6))) 
		          
		    
        
        
        	  
	            AND ((ARI.expiryDate IS NOT NULL AND ARI.expiryDate > now(6))
	        
        		
		  
             AND ARI.createDate >= #{minCreateDate} 
        
		  
             AND ARI.createDate < #{maxCreateDate} 
        
		  
             AND ARI.createOperatorId = #{createOperatorId} 
        			
		
			 AND ARI.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM SEC_AUTHREF ARI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND ARI.id = #{id} 
        			
		  
             AND ARI.refId = #{refId} 
        			
		  
             AND ARI.refType = #{refType} 
        
        
        	AND
        	
        		
        			ARI.refType = #{refTypeTemp,javaType=java.lang.String}
        			AND ARI.refId IN #{refIdTemp,javaType=java.lang.String}
        		
        	
        		
		  
             AND ARI.authId = #{authId} 
        		
          
        	  
		          
		             AND ((ARI.expiryDate IS NULL OR ARI.expiryDate <= now(6)) AND (ARI.effectiveDate IS NULL OR ARI.effective >= now(6))) 
		        
		          
		             AND ((ARI.expiryDate IS NOT NULL AND ARI.expiryDate > now(6)) AND (ARI.effectiveDate IS NOT NULL OR ARI.effective < now(6))) 
		          
		    
        
        
        	  
	            AND ((ARI.expiryDate IS NOT NULL AND ARI.expiryDate > now(6))
	        
        		
		  
             AND ARI.createDate >= #{minCreateDate} 
        
		  
             AND ARI.createDate < #{maxCreateDate} 
        
		  
             AND ARI.createOperatorId = #{createOperatorId} 
        			
		
			 AND ARI.id <> #{excludeId}
