mapper.select:
	SELECT 
			RRI.id,
			RRI.refType,
			RRI.refId,
			RRI.roleId,
			RRI.effectiveDate,
			RRI.expiryDate,
			RRI.createOperatorId,
			RRI.createDate,
			RRI.lastUpdateOperatorId,
			RRI.lastUpdateDate
	  FROM sec_roleref RRI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND RRI.id = #{id,javaType=java.lang.String}
        
		  
            AND RRI.refType = #{refType,javaType=java.lang.String}
        
		  
            AND RRI.refId = #{refId,javaType=java.lang.String}
        
        
        	AND
        	
        		
        			RRI.refType = #{refTypeTemp,javaType=java.lang.String}
        			AND RRI.refId IN #{refIdTemp,javaType=java.lang.String}
        		
        	
        
		  
            AND RRI.roleId = #{roleId,javaType=java.lang.String}
        
          
        	  
		          
		             AND ((RRI.expiryDate IS NULL OR RRI.expiryDate <= now(6)) AND (RRI.effectiveDate IS NULL OR RRI.effective >= now(6))) 
		        
		          
		             AND ((RRI.expiryDate IS NOT NULL AND RRI.expiryDate > now(6)) AND (RRI.effectiveDate IS NOT NULL OR RRI.effective < now(6))) 
		          
		    
        
        
        	  
	            AND ((RRI.expiryDate IS NOT NULL AND RRI.expiryDate > now(6))
	        
        
		
			  
	            AND RRI.role = #{role.id,javaType=java.lang.String}
	        
        
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM RoleRefItem TRRI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND RRI.id = #{id,javaType=java.lang.String}
        
		  
            AND RRI.refType = #{refType,javaType=java.lang.String}
        
		  
            AND RRI.refId = #{refId,javaType=java.lang.String}
        
        
        	AND
        	
        		
        			RRI.refType = #{refTypeTemp,javaType=java.lang.String}
        			AND RRI.refId IN #{refIdTemp,javaType=java.lang.String}
        		
        	
        
		  
            AND RRI.roleId = #{roleId,javaType=java.lang.String}
        
          
        	  
		          
		             AND ((RRI.expiryDate IS NULL OR RRI.expiryDate <= now(6)) AND (RRI.effectiveDate IS NULL OR RRI.effective >= now(6))) 
		        
		          
		             AND ((RRI.expiryDate IS NOT NULL AND RRI.expiryDate > now(6)) AND (RRI.effectiveDate IS NOT NULL OR RRI.effective < now(6))) 
		          
		    
        
        
        	  
	            AND ((RRI.expiryDate IS NOT NULL AND RRI.expiryDate > now(6))
	        
        
		
			  
	            AND RRI.role = #{role.id,javaType=java.lang.String}
