mapper.select:
	SELECT 
			TM.code,
			TM.lastUpdateDate,
			TM.lastUpdateOperatorId,
			TM.name,
			TM.testInt,
			TM.testLong,
			TM.type,
			TM.createDate,
			TM.createOperatorId,
			TM.remark,
			TM.nested1Id,
			TM.nested2Code,
			TM.attributes,
			TM.description
	  FROM TEST_TESTMODE2 TM
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND TM.code = #{code} 
        			
		  
             AND TM.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND TM.name = #{name} 
        			
		  
             AND TM.testInt = #{testInt} 
        			
		  
             AND TM.testLong = #{testLong} 
        			
		  
             AND TM.type = #{type} 
        			
		  
             AND TM.createDate >= #{minCreateDate} 
        
		  
             AND TM.createDate < #{maxCreateDate} 
        
		  
             AND TM.createOperatorId = #{createOperatorId} 
        			
		  
             AND TM.remark = #{remark} 
        			
		
			  
	             AND TM.nested1Id = #{nested1.id} 
	        
        			
		
			  
	             AND TM.nested2Code = #{nested2.code} 
	        
        			
		  
             AND TM.attributes = #{attributes} 
        			
		  
             AND TM.description = #{description} 
        			
		
			 AND TM.code <> #{excludeCode} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM TEST_TESTMODE2 TM
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND TM.code = #{code} 
        			
		  
             AND TM.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND TM.name = #{name} 
        			
		  
             AND TM.testInt = #{testInt} 
        			
		  
             AND TM.testLong = #{testLong} 
        			
		  
             AND TM.type = #{type} 
        			
		  
             AND TM.createDate >= #{minCreateDate} 
        
		  
             AND TM.createDate < #{maxCreateDate} 
        
		  
             AND TM.createOperatorId = #{createOperatorId} 
        			
		  
             AND TM.remark = #{remark} 
        			
		
			  
	             AND TM.nested1Id = #{nested1.id} 
	        
        			
		
			  
	             AND TM.nested2Code = #{nested2.code} 
	        
        			
		  
             AND TM.attributes = #{attributes} 
        			
		  
             AND TM.description = #{description} 
        			
		
			 AND TM.code <> #{excludeCode}
